CREATE TABLE `myshop`.`tbl_users` ( `user_id` INT(11) NOT NULL AUTO_INCREMENT COMMENT 'ID ผู้ใช้' , `username` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `password` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `user_name` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `user_phone` VARCHAR(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `user_mail` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `user_address` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `user_profile` VARCHAR(200) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'รูปโปรไฟล์' , `user_insert` VARCHAR(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'วันที่สมัคร' , `user_update` VARCHAR(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'วันที่อัปเดทล่าสุด' , PRIMARY KEY (`user_id`)) ENGINE = MyISAM CHARSET=utf8 COLLATE utf8_general_ci;